'use strict'

const generic = require('@nodeguy/generic')
const is = require('./is')

const to = generic.create({name: 'to', curry: true})

to.method([x => Object.is(x, Object), is(Date)],
  (type, date) => ({
    year: date.getUTCFullYear(),
    month: date.getUTCMonth(),
    date: date.getUTCDate(),
    hours: date.getUTCHours(),
    minutes: date.getUTCMinutes(),
    seconds: date.getUTCSeconds(),
    milliseconds: date.getUTCMilliseconds()
  })
)

to.method([x => Object.is(x, Date), is(Object)],
  (type, object) =>
    new Date(Date.UTC(
      object.year,
      object.month,
      object.date,
      object.hours,
      object.minutes,
      object.seconds,
      object.milliseconds
    ))
)

module.exports = to
