'use strict'

const assert = require('assert')
const has = require('../lib').has

describe('checks for the existence of properties in an object', function () {
  const object = {one: 1, two: 2, three: 3}

  it('finds them', function () {
    assert(has(['one', 'two', 'three'], object))
  })

  it("doesn't find them", function () {
    assert(!has(['missing'], object))
  })

  it('works with non-objects', function () {
    assert(!has(['foobar'], null))
  })
})
