'use strict'

const assert = require('assert')
const to = require('../lib/to')

it('converts a Date to an Object', function () {
  assert.deepEqual(to(Object, new Date(0)), {
    year: 1970,
    month: 0,
    date: 1,
    hours: 0,
    minutes: 0,
    seconds: 0,
    milliseconds: 0
  })
})

it('converts an Object to a Date', function () {
  assert.deepEqual(Number(to(Date, {
    year: 1970,
    month: 0,
    date: 1,
    hours: 0,
    minutes: 0,
    seconds: 0,
    milliseconds: 0
  })), 0)
})
