'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const add = generic.function({name: 'add', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-set.prototype.add
add.method([_, is(Set)],
  (value, set) =>
    set.add(value)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-weakset.prototype.add
add.method([_, is(WeakSet)],
  (value, weakSet) =>
    weakSet.add(value)
)

module.exports = add
