'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const anchor = generic.function({name: 'anchor', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.anchor
anchor.method([_, is(String)],
  (name, string) =>
    string.anchor(name)
)

module.exports = anchor
