'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const apply = generic.function({name: 'apply', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-function.prototype.apply
apply.method([_, _, is(Function)],
  (thisArg, argArray, func) =>
    func.apply(thisArg, argArray)
)

module.exports = apply
