'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const bind = generic.function({name: 'bind', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-function.prototype.bind
bind.method([_, _, is(Function)],
  (thisArg, args, func) =>
    func.bind(thisArg, args)
)

module.exports = bind
