'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const call = generic.function({name: 'call', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-function.prototype.call
call.method([_, _, is(Function)],
  (thisArg, args, func) =>
    func.call(thisArg, args)
)

module.exports = call
