'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const catchFunction = generic.function({name: 'catch', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-promise.prototype.catch
catchFunction.method([_, is(Promise)],
  (onRejected, promise) =>
    promise.catchFunction(onRejected)
)

module.exports = catchFunction
