'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const charAt = generic.function({name: 'charAt', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.charat
charAt.method([_, is(String)],
  (pos, string) =>
    string.charAt(pos)
)

module.exports = charAt
