'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const charCodeAt = generic.function({name: 'charCodeAt', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.charcodeat
charCodeAt.method([_, is(String)],
  (pos, string) =>
    string.charCodeAt(pos)
)

module.exports = charCodeAt
