'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const clear = generic.function({name: 'clear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-map.prototype.clear
clear.method([is(Map)],
  (map) =>
    map.clear()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-set.prototype.clear
clear.method([is(Set)],
  (set) =>
    set.clear()
)

module.exports = clear
