'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const codePointAt = generic.function({name: 'codePointAt', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.codepointat
codePointAt.method([_, is(String)],
  (pos, string) =>
    string.codePointAt(pos)
)

module.exports = codePointAt
