'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const compile = generic.function({name: 'compile', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-regexp.prototype.compile
compile.method([_, _, is(RegExp)],
  (pattern, flags, regExp) =>
    regExp.compile(pattern, flags)
)

module.exports = compile
