'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const endsWith = generic.function({name: 'endsWith', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.endswith
endsWith.method([_, _, is(String)],
  (searchString, endPosition, string) =>
    string.endsWith(searchString, endPosition)
)

module.exports = endsWith
