'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const every = generic.function({name: 'every', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.every
every.method([_, is(Array)],
  (callbackfn, array) =>
    array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Int8Array)],
  (callbackfn, int8Array) =>
    int8Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Uint8Array)],
  (callbackfn, uint8Array) =>
    uint8Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Uint8ClampedArray)],
  (callbackfn, uint8ClampedArray) =>
    uint8ClampedArray.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Int16Array)],
  (callbackfn, int16Array) =>
    int16Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Uint16Array)],
  (callbackfn, uint16Array) =>
    uint16Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Int32Array)],
  (callbackfn, int32Array) =>
    int32Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Uint32Array)],
  (callbackfn, uint32Array) =>
    uint32Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Float32Array)],
  (callbackfn, float32Array) =>
    float32Array.every(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.every
every.method([_, is(Float64Array)],
  (callbackfn, float64Array) =>
    float64Array.every(callbackfn)
)

module.exports = every
