'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const exec = generic.function({name: 'exec', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-regexp.prototype.exec
exec.method([_, is(RegExp)],
  (string, regExp) =>
    regExp.exec(string)
)

module.exports = exec
