'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const fill = generic.function({name: 'fill', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.fill
fill.method([_, _, _, is(Array)],
  (value, start, end, array) =>
    array.fill(value, start, end)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.fill
fill.method([_, _, _, is(Int8Array)],
  (value, start, end, int8Array) =>
    int8Array.fill(value, start, end)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.fill
fill.method([_, _, _, is(Uint8Array)],
  (value, start, end, uint8Array) =>
    uint8Array.fill(value, start, end)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.fill
fill.method([_, _, _, is(Uint8ClampedArray)],
  (value, start, end, uint8ClampedArray) =>
    uint8ClampedArray.fill(value, start, end)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.fill
fill.method([_, _, _, is(Int16Array)],
  (value, start, end, int16Array) =>
    int16Array.fill(value, start, end)
)

