'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const filter = generic.function({name: 'filter', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.filter
filter.method([_, is(Array)],
  (callbackfn, array) =>
    array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Int8Array)],
  (callbackfn, int8Array) =>
    int8Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Uint8Array)],
  (callbackfn, uint8Array) =>
    uint8Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Uint8ClampedArray)],
  (callbackfn, uint8ClampedArray) =>
    uint8ClampedArray.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Int16Array)],
  (callbackfn, int16Array) =>
    int16Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Uint16Array)],
  (callbackfn, uint16Array) =>
    uint16Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Int32Array)],
  (callbackfn, int32Array) =>
    int32Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Uint32Array)],
  (callbackfn, uint32Array) =>
    uint32Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Float32Array)],
  (callbackfn, float32Array) =>
    float32Array.filter(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.filter
filter.method([_, is(Float64Array)],
  (callbackfn, float64Array) =>
    float64Array.filter(callbackfn)
)

module.exports = filter
