'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const fontcolor = generic.function({name: 'fontcolor', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.fontcolor
fontcolor.method([_, is(String)],
  (color, string) =>
    string.fontcolor(color)
)

module.exports = fontcolor
