'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const fontsize = generic.function({name: 'fontsize', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.fontsize
fontsize.method([_, is(String)],
  (size, string) =>
    string.fontsize(size)
)

module.exports = fontsize
