'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const get = generic.function({name: 'get', curry: true})

get.method([is(String), is(Object)],
  (key, object) =>
    object[key]
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-map.prototype.get
get.method([_, is(Map)],
  (key, map) =>
    map.get(key)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-weakmap.prototype.get
get.method([_, is(WeakMap)],
  (key, weakMap) =>
    weakMap.get(key)
)

module.exports = get
