'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getDate = generic.function({name: 'getDate', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getdate
getDate.method([is(Date)],
  (date) =>
    date.getDate()
)

module.exports = getDate
