'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getDay = generic.function({name: 'getDay', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getday
getDay.method([is(Date)],
  (date) =>
    date.getDay()
)

module.exports = getDay
