'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getFloat32 = generic.function({name: 'getFloat32', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getfloat32
getFloat32.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getFloat32(byteOffset, littleEndian)
)

module.exports = getFloat32
