'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getFloat64 = generic.function({name: 'getFloat64', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getfloat64
getFloat64.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getFloat64(byteOffset, littleEndian)
)

module.exports = getFloat64
