'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getFullYear = generic.function({name: 'getFullYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getfullyear
getFullYear.method([is(Date)],
  (date) =>
    date.getFullYear()
)

module.exports = getFullYear
