'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getHours = generic.function({name: 'getHours', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.gethours
getHours.method([is(Date)],
  (date) =>
    date.getHours()
)

module.exports = getHours
