'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getInt16 = generic.function({name: 'getInt16', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getint16
getInt16.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getInt16(byteOffset, littleEndian)
)

module.exports = getInt16
