'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getInt32 = generic.function({name: 'getInt32', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getint32
getInt32.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getInt32(byteOffset, littleEndian)
)

module.exports = getInt32
