'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getInt8 = generic.function({name: 'getInt8', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getint8
getInt8.method([_, is(DataView)],
  (byteOffset, dataView) =>
    dataView.getInt8(byteOffset)
)

module.exports = getInt8
