'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getMilliseconds = generic.function({name: 'getMilliseconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getmilliseconds
getMilliseconds.method([is(Date)],
  (date) =>
    date.getMilliseconds()
)

module.exports = getMilliseconds
