'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getMinutes = generic.function({name: 'getMinutes', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getminutes
getMinutes.method([is(Date)],
  (date) =>
    date.getMinutes()
)

module.exports = getMinutes
