'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getMonth = generic.function({name: 'getMonth', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getmonth
getMonth.method([is(Date)],
  (date) =>
    date.getMonth()
)

module.exports = getMonth
