'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getSeconds = generic.function({name: 'getSeconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getseconds
getSeconds.method([is(Date)],
  (date) =>
    date.getSeconds()
)

module.exports = getSeconds
