'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getTime = generic.function({name: 'getTime', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.gettime
getTime.method([is(Date)],
  (date) =>
    date.getTime()
)

module.exports = getTime
