'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getTimezoneOffset = generic.function({name: 'getTimezoneOffset', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.gettimezoneoffset
getTimezoneOffset.method([is(Date)],
  (date) =>
    date.getTimezoneOffset()
)

module.exports = getTimezoneOffset
