'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCDate = generic.function({name: 'getUTCDate', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcdate
getUTCDate.method([is(Date)],
  (date) =>
    date.getUTCDate()
)

module.exports = getUTCDate
