'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCDay = generic.function({name: 'getUTCDay', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcday
getUTCDay.method([is(Date)],
  (date) =>
    date.getUTCDay()
)

module.exports = getUTCDay
