'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCFullYear = generic.function({name: 'getUTCFullYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcfullyear
getUTCFullYear.method([is(Date)],
  (date) =>
    date.getUTCFullYear()
)

module.exports = getUTCFullYear
