'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCHours = generic.function({name: 'getUTCHours', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutchours
getUTCHours.method([is(Date)],
  (date) =>
    date.getUTCHours()
)

module.exports = getUTCHours
