'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCMilliseconds = generic.function({name: 'getUTCMilliseconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcmilliseconds
getUTCMilliseconds.method([is(Date)],
  (date) =>
    date.getUTCMilliseconds()
)

module.exports = getUTCMilliseconds
