'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCMinutes = generic.function({name: 'getUTCMinutes', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcminutes
getUTCMinutes.method([is(Date)],
  (date) =>
    date.getUTCMinutes()
)

module.exports = getUTCMinutes
