'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCMonth = generic.function({name: 'getUTCMonth', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcmonth
getUTCMonth.method([is(Date)],
  (date) =>
    date.getUTCMonth()
)

module.exports = getUTCMonth
