'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getUTCSeconds = generic.function({name: 'getUTCSeconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getutcseconds
getUTCSeconds.method([is(Date)],
  (date) =>
    date.getUTCSeconds()
)

module.exports = getUTCSeconds
