'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getUint16 = generic.function({name: 'getUint16', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getuint16
getUint16.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getUint16(byteOffset, littleEndian)
)

module.exports = getUint16
