'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getUint32 = generic.function({name: 'getUint32', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getuint32
getUint32.method([_, _, is(DataView)],
  (byteOffset, littleEndian, dataView) =>
    dataView.getUint32(byteOffset, littleEndian)
)

module.exports = getUint32
