'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const getUint8 = generic.function({name: 'getUint8', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.getuint8
getUint8.method([_, is(DataView)],
  (byteOffset, dataView) =>
    dataView.getUint8(byteOffset)
)

module.exports = getUint8
