'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const getYear = generic.function({name: 'getYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.getyear
getYear.method([is(Date)],
  (date) =>
    date.getYear()
)

module.exports = getYear
