'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const has = generic.function({name: 'has', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-map.prototype.has
has.method([_, is(Map)],
  (key, map) =>
    map.has(key)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-weakmap.prototype.has
has.method([_, is(WeakMap)],
  (key, weakMap) =>
    weakMap.has(key)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-set.prototype.has
has.method([_, is(Set)],
  (value, set) =>
    set.has(value)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-weakset.prototype.has
has.method([_, is(WeakSet)],
  (value, weakSet) =>
    weakSet.has(value)
)

module.exports = has
